mapper.select:
SELECT u.id AS 'userId', u.name AS 'userName', SUM(IFNULL(frm.write_bytes,0) + IFNULL(frd.write_bytes,0) + IFNULL(frm2.write_bytes,0)) AS 'upFlowBytes', SUM(IFNULL(frm.read_bytes,0) + IFNULL(frd.read_bytes,0) + IFNULL(frm2.read_bytes,0)) AS 'downFlowBytes', IFNULL(SUM(frm.write_bytes),0) monthWriteBytes, IFNULL(SUM(frm.read_bytes),0) monthReadBytes, IFNULL(SUM(frd.write_bytes),0) dayWriteBytes, IFNULL(SUM(frd.read_bytes),0) dayReadBytes, IFNULL(SUM(frm2.write_bytes),0) minuteWriteBytes, IFNULL(SUM(frm2.read_bytes),0) minuteReadBytes FROM `user` u LEFT JOIN (SELECT user_id,sum(write_bytes) write_bytes,sum(read_bytes) read_bytes from flow_report_month GROUP BY user_id) frm ON u.id = frm.user_id LEFT JOIN (SELECT user_id,sum(write_bytes) write_bytes,sum(read_bytes) read_bytes from flow_report_day WHERE date >= #{curMonthBeginDate} AND date <= #{curDayBeginDate} GROUP BY user_id) frd ON u.id = frd.user_id LEFT JOIN (SELECT user_id,sum(write_bytes) write_bytes,sum(read_bytes) read_bytes from flow_report_minute WHERE date >= #{curDayBeginDate} AND date <= #{curDate} GROUP BY user_id) frm2 ON u.id = frm2.user_id
AND u.id = #{userId}
GROUP BY u.id

SELECT l.id AS 'licenseId', l.name AS 'licenseName', u.id AS 'userId', u.name AS 'userName', SUM(IFNULL(frm.write_bytes,0) + IFNULL(frd.write_bytes,0) + IFNULL(frm2.write_bytes,0)) AS 'upFlowBytes', SUM(IFNULL(frm.read_bytes,0) + IFNULL(frd.read_bytes,0) + IFNULL(frm2.read_bytes,0)) AS 'downFlowBytes', IFNULL(SUM(frm.write_bytes),0) monthWriteBytes, IFNULL(SUM(frm.read_bytes),0) monthReadBytes, IFNULL(SUM(frd.write_bytes),0) dayWriteBytes, IFNULL(SUM(frd.read_bytes),0) dayReadBytes, IFNULL(SUM(frm2.write_bytes),0) minuteWriteBytes, IFNULL(SUM(frm2.read_bytes),0) minuteReadBytes FROM `license` l LEFT JOIN `user` u ON l.user_id = u.id LEFT JOIN (SELECT license_id,sum(write_bytes) write_bytes,sum(read_bytes) read_bytes from flow_report_month GROUP BY license_id) frm ON l.id = frm.license_id LEFT JOIN (SELECT license_id,sum(write_bytes) write_bytes,sum(read_bytes) read_bytes from flow_report_day WHERE date >= #{curMonthBeginDate} AND #{curDayBeginDate} GROUP BY license_id) frd ON l.id = frd.license_id LEFT JOIN (SELECT license_id,sum(write_bytes) write_bytes,sum(read_bytes) read_bytes from flow_report_minute WHERE date >= #{curDayBeginDate} AND date <= #{curDate} GROUP BY license_id)frm2 ON l.id = frm2.license_id
AND u.id = #{userId}
GROUP BY l.id

SELECT T2.user_id, u.name AS 'userName', T2.date, T2.upFlowBytes, T2.downFlowBytes FROM (
SELECT frm.user_id, frm.date, IFNULL(SUM(frm.write_bytes),0) AS 'upFlowBytes', IFNULL(SUM(frm.read_bytes),0) AS 'downFlowBytes' FROM flow_report_month frm GROUP BY frm.user_id,frm.date
UNION ALL
SELECT T1.user_id, #{curMonthBeginDate} AS 'date', IFNULL(SUM(T1.upFlowBytes),0) AS 'upFlowBytes', IFNULL(SUM(T1.downFlowBytes),0) AS 'downFlowBytes' FROM (
SELECT frd.user_id, frd.license_id, frd.write_bytes AS 'upFlowBytes', frd.read_bytes AS 'downFlowBytes', frd.date FROM flow_report_day frd WHERE frd.date >= #{curMonthBeginDate} AND frd.date <= #{curDayBeginDate}
UNION ALL
SELECT frm.user_id, frm.license_id, IFNULL(SUM(frm.write_bytes),0) AS 'upFlowBytes', IFNULL(SUM(frm.read_bytes),0) AS 'downFlowBytes', #{curDayBeginDate} AS 'date' FROM flow_report_minute frm WHERE frm.date >= #{curDayBeginDate} AND frm.date <= #{curDate} GROUP BY frm.user_id,frm.license_id
) T1 GROUP BY T1.user_id
) T2 LEFT JOIN `user` u ON u.id = T2.user_id
AND u.id = #{userId}
ORDER BY T2.user_id ASC,T2.date DESC

SELECT T2.user_id, u.name AS 'userName', T2.license_id, l.`name` AS 'licenseName', T2.date, T2.upFlowBytes, T2.downFlowBytes FROM (
SELECT frm.user_id, frm.license_id, frm.date, IFNULL(SUM(frm.write_bytes),0) AS 'upFlowBytes', IFNULL(SUM(frm.read_bytes),0) AS 'downFlowBytes' FROM flow_report_month frm GROUP BY frm.user_id,frm.license_id,frm.date
UNION ALL
SELECT T1.user_id, T1.license_id, #{curMonthBeginDate} AS 'date', IFNULL(SUM(T1.upFlowBytes),0) AS 'upFlowBytes', IFNULL(SUM(T1.downFlowBytes),0) AS 'downFlowBytes' FROM (
SELECT frd.user_id, frd.license_id, frd.write_bytes AS 'upFlowBytes', frd.read_bytes AS 'downFlowBytes', frd.date FROM flow_report_day frd WHERE frd.date >= #{curMonthBeginDate} AND frd.date <= #{curDayBeginDate}
UNION ALL
SELECT frm.user_id, frm.license_id, IFNULL(SUM(frm.write_bytes),0) AS 'upFlowBytes', IFNULL(SUM(frm.read_bytes),0) AS 'downFlowBytes', #{curDayBeginDate} AS 'date' FROM flow_report_minute frm WHERE frm.date >= #{curDayBeginDate} AND frm.date <= #{curDate} GROUP BY frm.user_id,frm.license_id
) T1 GROUP BY T1.user_id,T1.license_id
) T2 LEFT JOIN `user` u ON u.id = T2.user_id LEFT JOIN `license` l ON l.id = T2.license_id
AND u.id = #{userId}
AND l.id = #{licenseId}
ORDER BY T2.user_id ASC,T2.license_id,T2.date DESC

SELECT IFNULL(SUM(T.upFlowBytes),0) AS 'upFlowBytes', IFNULL(SUM(T.downFlowBytes),0) AS 'downFlowBytes' FROM (
SELECT IFNULL(SUM(frm.write_bytes),0) AS 'upFlowBytes', IFNULL(SUM(frm.read_bytes),0) AS 'downFlowBytes' FROM flow_report_month frm UNION ALL
SELECT IFNULL(SUM(frd.write_bytes),0) AS 'upFlowBytes', IFNULL(SUM(frd.read_bytes),0) AS 'downFlowBytes' FROM flow_report_day frd WHERE frd.date >= #{curMonthBeginDate} AND frd.date <= #{curDayBeginDate}
UNION ALL
SELECT IFNULL(SUM(frm.write_bytes),0) AS 'upFlowBytes', IFNULL(SUM(frm.read_bytes),0) AS 'downFlowBytes' FROM flow_report_minute frm WHERE frm.date >= #{curDayBeginDate} AND frm.date <= #{curDate}
) T

SELECT T.* FROM (
SELECT frd.date, IFNULL(SUM(frd.write_bytes),0) AS 'upFlowBytes', IFNULL(SUM(frd.read_bytes),0) AS 'downFlowBytes' FROM flow_report_day frd WHERE frd.date >= #{beginDate} AND frd.date <= #{curDayBeginDate} GROUP BY frd.date
UNION ALL
SELECT #{curDayBeginDate} AS 'date', IFNULL(SUM(frm.write_bytes),0) AS 'upFlowBytes', IFNULL(SUM(frm.read_bytes),0) AS 'downFlowBytes' FROM flow_report_minute frm WHERE frm.date >= #{curDayBeginDate} AND frm.date <= #{curDate}
) T
